import React, { Component } from 'react';
import './App.css';
import {Route, Link} from 'react-router-dom'
import SearchForm from './SearchForm' 
import SearchResults from './SearchResults'
import About from './About'
import axios from 'axios';

const NEWS_API = `https://newsapi.org/v2/everything?apiKey=0f934fdb8888409689c62800a41196d4`


class App extends Component {
  constructor(props) {
    super(props) 

    this.state = {
      headline: [],
      inputValue: ''    
    }
  }

  fetchNewsData = async (url, params) => {
      let stringURL
      params ? stringURL = url + '&q=' + params : stringURL = url
      const response = await axios(stringURL)
      console.log(response.data)
      this.setState({
        headline: response.data
      })
      // console.log(this.state.headline)
  }

  componentDidMount() {
    this.fetchNewsData(NEWS_API, 'q=obama')

  }

  handleChange = (event) => {
    event.preventDefault()
    // console.log(event.target.searchBar.value)
    const keyWord = event.target.searchBar.value
    this.fetchNewsData(NEWS_API, keyWord)
    // this.setState({
    //   inputValue: event.target.value,  
    // })
  }

  handleClick = () => {
    const articleList = this.response.data
    console.log(articleList)
  }

  // filterNewsData = (event) => {
  //   event.preventDefault()
  //   return this.state.headline.articles.filter(subject => subject.)
  // }

  render() {
    // console.log(this.state.headline)
    // console.log(this.state)
    return (
      <div className="App">
        <nav>
          <Link to="/home">Home</Link>
          <Link to="/archives">Archives</Link>
          <Link to="/about">About</Link>
        </nav>
        <main>
          <Route 
            path="/home" 
            render={() => <SearchForm 
              handleChange={this.handleChange} 
              headline={this.state.headline} 
              filterNewsData={this.filterNewsData} />} 
            />
            <Route path="/home" render={() => <SearchResults 
              headline={this.state.headline} />} 
              handleClick={this.handleClick}
            />
          {/* <SearchForm handleChange={this.handleChange} headline={this.state} /> */}
          <Route path="/about" component={About}/>
        </main>
      </div>
    );
  }
}

export default App;
