import React, { Component } from 'react';
import './App.css';
import {Route, Link} from 'react-router-dom'
import SearchForm from './SearchForm' 
import SearchResults from './SearchResults'
import About from './About'
import axios from 'axios';

const NEWS_API = `https://newsapi.org/v2/everything?q=tarrif&apiKey=0f934fdb8888409689c62800a41196d4`


class App extends Component {
  constructor(props) {
    super(props) 

    this.state = {
      headline: [],
      inputValue: ''
    }
  }

  fetchNewsData = async () => {
      const response = await axios(NEWS_API)
      console.log(response.data)
      this.setState({
        headline: response.data
      })
      console.log(this.state.headline)
  }

  componentDidMount() {
    this.fetchNewsData()
  }

  handleChange = (event) => {
    // console.log(event.target.value)
    this.setState({
      inputValue: event.target.value
    })
  }

  filterNewsData = () => {

  }

  render() {
    // console.log(this.state.headline)
    // console.log(this.state)
    return (
      <div className="App">
        <nav>
          <Link to="/home">Home</Link>
          <Link to="/archives">Archives</Link>
          <Link to="/about">About</Link>
        </nav>
        <main>
          <Route path="/searchresults" render={() => <SearchResults headline={this.state.headline} />} />
          <Route path="/home" render={() => <SearchForm handleChange={this.handleChange} headline={this.state.headline} />} />
          {/* <SearchForm handleChange={this.handleChange} headline={this.state} /> */}
          <Route path="/about" component={About}/>
        </main>
      </div>
    );
  }
}

export default App;
